@Override
public Floor getFetchedFloor(int level) {
    List<Floor> floors = em.createNamedQuery("Floor.fetchFloor",
            Floor.class)
            .setParameter("level", level)
            .getResultList();
    return floors.isEmpty() ? null : floors.get(0);
}

